// Score.js
import { useState } from 'react';

export function Score({ setSelectedScore }) {
  const [selectedScore, setSelectedScoreLocal] = useState(null);

  const handleScoreClick = (score) => {
    setSelectedScoreLocal(score);
    setSelectedScore(score);
  };

  return (
    <div className="score-container">
      {[1, 2, 3, 4, 5].map((score) => (
        <button
          key={score}
          className={selectedScore === score ? 'active' : 'inactive'}
          onClick={() => handleScoreClick(score)}
        >
          {score}
        </button>
      ))}
    </div>
  );
}
